class InvalidEntryPointError extends Error {
    constructor(message) {
        super(message); 
        this.name = "InvalidEntryPointError";
    }
}

class InvalidDirectoryPathError extends Error {
    constructor(message) {
        super(message); 
        this.name = "InvalidDirectoryPathError";
    }
}

class WebpackError extends Error {
    constructor(message) {
        super(message); 
        this.name = "WebpackError";
    }
}

class ClosureCompilerError extends Error {
    constructor(message) {
        super(message); 
        this.name = "ClosureCompilerError";
    }
}


module.exports = { 
    InvalidEntryPointError: InvalidEntryPointError, 
    InvalidDirectoryPathError: InvalidDirectoryPathError,
    WebpackError: WebpackError,
    ClosureCompilerError: ClosureCompilerError
};
